mapper.update:
UPDATE logstash_machine
SET config_content = #{configContent},
    update_time    = NOW(),
    update_user    = #{updateUser}
WHERE id = #{id}

UPDATE logstash_machine
SET jvm_options = #{jvmOptions},
    update_time = NOW(),
    update_user = #{updateUser}
WHERE id = #{id}

UPDATE logstash_machine
SET logstash_yml = #{logstashYml},
    update_time  = NOW(),
    update_user  = #{updateUser}
WHERE id = #{id}

mapper.select:
SELECT
FROM logstash_machine WHERE id IN
#{id}

SELECT

FROM logstash_machine
WHERE machine_id = #{machineId}
AND deploy_path = #{deployPath}
